import axios from "axios";
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { getToken, setToken, setUserDetails } from "../helper/SessionHelper";
import { Hideloader, Showloader } from "../redux/state-slice/settingsSlice";
import { SetCanceledTask, SetNewTask, SetProgressTask } from "../redux/state-slice/taskSlice";
import store from "../redux/store/store";

const BaseUrl = "http://localhost:8000/api/v1";
const AxiosHeader={headers:{'token':getToken()}}

export function RegistrationRequest(
  email,
  firstName,
  lastName,
  mobile,
  password
) {
  //call start
  store.dispatch(Showloader());
  let URL = BaseUrl + "/register";
  let PostBody = {
    email: email,
    firstName: firstName,
    lastName: lastName,
    mobile: mobile,
    password: password,
  };

  return axios
    .post(URL, PostBody)
    .then((res) => {
            store.dispatch(Hideloader());
        if (res.status === 200) {
            
            if(res.data.status==='fail'){
                
                if(res.data.data.keyPattern.email===1){

                    toast.warn(' Email Already Exist', {
                        position: "bottom-center",
                        autoClose: 4000,
                        hideProgressBar: false,
                        closeOnClick: true,
                        pauseOnHover: true,
                        draggable: true,
                        progress: undefined,
                        theme: "light",
                        });
                    return false
                    
                }
           
            }else{
                toast.success('Registration Successfull!', {
                    position: "bottom-center",
                    autoClose: 5000,
                    hideProgressBar: false,
                    closeOnClick: true,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                    theme: "light",
                    });
                return true
            } 
        }else{
            return false
        }
    })
    .catch((err) => {
      //call end
      store.dispatch(Hideloader());   
      return false;
    });
}
export function LoginRequest(email,password) {
  //call start
  store.dispatch(Showloader());
  let URL = BaseUrl + "/login";
  let PostBody = {email: email,password: password};

  return axios
    .post(URL, PostBody)
    .then((res) => {
            store.dispatch(Hideloader());
        if (res.status === 200) {
            
            setToken(res.data.token);
            setUserDetails(res.data.data) 
            toast.success('Login Successfull!', {
                position: "bottom-center",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
                theme: "light",
                });
            return true
        }else{
            toast.error('Email or Password Not Match Api',{
                position: "bottom-center"})
            return false
        }
    })
    .catch((err) => {
      //call end
      store.dispatch(Hideloader());   
      return false;
    });
}


export function CreateTaskRequest(
    title, description) {
    //call start
    store.dispatch(Showloader());
    let URL = BaseUrl + "/createTask";
    let PostBody = {
        title: title,
        description: description,
        status: "New"
    };
  
    return axios
      .post(URL, PostBody,AxiosHeader)
      .then((res) => {
              store.dispatch(Hideloader());
          if (res.status === 200) {
            toast.success('Task create Successfull!', {
                position: "bottom-center",
                autoClose: 5000,
                hideProgressBar: false,
                closeOnClick: true,
                pauseOnHover: true,
                draggable: true,
                progress: undefined,
                theme: "light",
                });
                return true
              
          }else{
            toast.error('Task Not Create. Someting wrong!',{
                position: "bottom-center"})
              return false
          }
      })
      .catch((err) => {
        //call end
        store.dispatch(Hideloader());   
        return false;
      });
  }

  export function SelectByStatus(Status){
    let URL=BaseUrl+"/selectByStatus/"+Status;
    store.dispatch(Showloader());
    return axios.get(URL,AxiosHeader).then(res=>{
        store.dispatch(Hideloader());
        if(res.status===200){
            if(Status==="New"){
                store.dispatch(SetNewTask(res.data.data))
            }else if(Status==="Completed"){
                store.dispatch(SetNewTask(res.data.data))
            }else if(Status==="Canceled"){
                store.dispatch(SetCanceledTask(res.data.data))
            }else if(Status==="Progress"){
                store.dispatch(SetProgressTask(res.data.data))
            }
        }
    })
  }
