            toast.error('email required',{
              position: "bottom-center"})
          }else{
            RecoveryEmailRequest(email).then(result=>{
              if(result===true){

              }
            })
        }
    }
  return (
    <Fragment>
    <div className='login__wrapper'>
      <div className="login__form__wrapper">
