            <h2>Enter Email</h2>
          <div className="login__field">
            <input ref={(input)=>emailRef=input} type="text" />
            
            <input onClick={onSubmit} className='login__btn' type="button" value="Next" />
          </div>
          
          
        </div>
      </div>
    </div>
      
    </Fragment>
  )
}

export default SendOTP
