import React, { useEffect, useRef, useState } from 'react'
import { useSelector } from 'react-redux'
import { getSearchData, SelectByStatus } from '../../API/APIRequest'
import TaskCard from '../TaskCard/TaskCard'

const NewTask = () => {
  const[searchData,setSearchData]=useState([])
  let serchRef=useRef()
  useEffect(()=>{
    SelectByStatus("New")
  },[])

  const searchHandle=()=>{
    let searchKey=serchRef.value;
    if(searchKey.length>1){
      getSearchData(searchKey,"New").then(res=>{
        if(res===[]){
          setSearchData(null)
        }else{
          setSearchData(res)
        }
        
      })
    }
  }
console.log(searchData)
  const NewTaskData=useSelector((state)=>state.task.New)

  if(searchData.length>0){
    return (
      <div className='dashboard'>
        <div className="dashboard__wrapper">
          <div className="dashboard__search__task">
            <div className="task__status__title">
              <h2>New Task</h2>
            </div>
            <div className="task__search__bar">
            <input ref={(input)=>serchRef=input} onChange={searchHandle} type="text" placeholder='search or type'/>
            <span><i class="ri-search-line"></i></span>
            </div>
          </div>
        <div className="todo__task-wrapper">
          {
            searchData.map((item,i)=>(
              <TaskCard key={i} item={item}/>
            ))
          }
  
  </div>
        </div>
      </div>
    )
  }else{
    return (
      <div className='dashboard'>
        <div className="dashboard__wrapper">
          <div className="dashboard__search__task">
            <div className="task__status__title">
              <h2>New Task</h2>
            </div>
            <div className="task__search__bar">
            <input ref={(input)=>serchRef=input} onChange={searchHandle} type="text" placeholder='search or type'/>
            <span><i class="ri-search-line"></i></span>
            </div>
          </div>
        <div className="todo__task-wrapper">
          {
            NewTaskData.map((item,i)=>(
              <TaskCard key={i} item={item}/>
            ))
          }
  
  </div>
        </div>
      </div>
    )
  }
  
  
}

export default NewTask
