import React, { useRef } from 'react'

const CreateTask = () => {
  let titleRef,disRef=useRef();
  const onSubmit=()=>{
    let title=titleRef.value;
    let discription=disRef.value;
  }
  return (
    <div className='dashboard'>
    <div className="dashboard__wrapper">
    <div className="create__task-wrapper">
        <div className="create__task-form">
            <div className="field">
                <h4>Create New Task</h4>
            <input ref={(input)=>titleRef=input} type="text" placeholder='Task Name'/>
            <textarea name="" id="" cols="30" rows="10" placeholder='Task Description'></textarea>
            <p className='p_input'><input type="button" value="Submit" /></p>
            </div>
        </div>
    </div>
    </div>
  </div>
  )
}

export default CreateTask
